import React,{useState} from 'react'
import "./User.css"
import Card from './Card'

export default function User(props) {

    const [enteredUserName,setEnteredUserName]=useState('')
    const [enteredAge,setEnteredAge]=useState('')

    const addUserHandler=(event)=>{
        event.preventDefault()
        props.onUser(enteredUserName,enteredAge)

        setEnteredUserName('')
        setEnteredAge('')
        if(enteredUserName.trim().length === 0 || enteredAge.trim().length ===0){
           return;     
        }
        if(+enteredAge >1){

            return;
        }  
        props.onUser(enteredUserName,enteredAge)
        setEnteredUserName('')
        setEnteredAge('')

    }
    const userNameChange=function(event){
        setEnteredUserName(event.target.value)
    } 
    const AgeChange=function(event){
        setEnteredAge(event.target.value)
    }




    return (
        <Card>
    <form  onSubmit={addUserHandler} className='AddUser'>

        <label htmlFor="UserName">UserName</label>
<input type='text' id="UserName" value={enteredUserName} className='Texts' onChange={userNameChange}/>

   <label htmlFor="Age">Age(years)</label>
<input type='number' id="Age"className='Texts' value={enteredAge} onChange={AgeChange}/>

<button className='butten' onClick={addUserHandler}> Add User</button>


    </form>

    </Card>  
  )
}
